CREATE TABLE TaiKhoan (ID int IDENTITY(1,1) NOT NULL, Username varchar(255) NULL, Password varchar(255) NULL, PRIMARY KEY (ID));
CREATE TABLE GiaoVien (MaGV varchar(255) NOT NULL, TaiKhoanID int NOT NULL, HoTen nvarchar(255) NULL, Sdt varchar(255) NULL, DiaChi nvarchar(255) NULL, Email varchar(255) NULL, PRIMARY KEY (MaGV));
CREATE TABLE MonHoc (ID int IDENTITY(1,1) NOT NULL, Ten nvarchar(255) NULL, PRIMARY KEY (ID));
CREATE TABLE SinhVien (MaSV varchar(255) NOT NULL, HoTen nvarchar(255) NULL, Lop varchar(255) NULL, DiaChi nvarchar(255) NULL, Sdt varchar(255) NULL, Email varchar(255) NULL, PRIMARY KEY (MaSV));
CREATE TABLE LopHocPhan (ID int IDENTITY(1,1) NOT NULL, GiaoVienMaGV varchar(255) NOT NULL, MonHocID int NOT NULL, Phong nvarchar(255) NULL, KhoaHoc varchar(255) NULL, KyHoc int NOT NULL, Thu nvarchar(255) NULL, Kip int NOT NULL, PRIMARY KEY (ID));
CREATE TABLE DiemMonHoc (ID int IDENTITY(1,1) NOT NULL, MonHocID int NOT NULL, SinhVienMaSV varchar(255) NOT NULL, DiemCC float(10) NOT NULL, DiemBT float(10) NOT NULL, DiemGK float(10) NOT NULL, DiemCK float(10) NOT NULL, PRIMARY KEY (ID));
CREATE TABLE SinhVienLHP (ID int IDENTITY(1,1) NOT NULL, LopHocPhanID int NOT NULL, SinhVienMaSV varchar(255) NOT NULL, PRIMARY KEY (ID));
